const fetch = require('node-fetch');
const url = 'https://api.goodleap.com/rest/v2/categories';
const options = {
  method: 'GET',
  headers: {
    'authorization': 'Basic ' + Buffer.from(YOUR_API_KEY_ID + ':' + YOUR_API_KEY_SECRET).toString('base64'),
    'content-type': 'application/json',
    'cache-control': 'no-cache',
  },

};

try {
  const response = await fetch(url, options);

  if (response.ok) { 
    const data = await response.json();
    console.log(data);
  } 
} catch (err) {
  console.error('An error has occurred', err);
}

